mapper.select:
select mexe_exemodelo.mexe_secue, mexe_exemodelo.mod_secue, mod_modelo.mod_nombre, mexe_exemodelo.mexe_fecha, mexe_exemodelo.mexe_hora, mexe_exemodelo.mexe_estado, mexe_exemodelo.mexe_descripcion, mexe_exemodelo.mexe_pickle from mexe_exemodelo, mod_modelo
mexe_exemodelo.mod_secue = mod_modelo.mod_secue
and mod_nombre = #{mod_nombre}
and mexe_fecha like #{mexe_fecha}
and mexe_estado = #{mexe_estado}
order by mexe_exemodelo.mexe_secue limit 500;
