mapper.select:
SELECT usu_usuario.usu_cod, usu_usuario.per_cod, usu_usuario.usu_nombre, usu_usuario.usu_fch_cr, usu_usuario.usu_estado, usu_usuario.usu_clave, per_perfil.per_nombre FROM usu_usuario, per_perfil
usu_usuario.per_cod = per_perfil.per_cod
and usu_usuario.usu_cod = #{codigo}
and (usu_usuario.usu_clave = md5(#{clave}) or usu_usuario.usu_clave = #{clave})
and usu_usuario.usu_estado = #{estado}
order by usu_usuario.usu_nombre asc limit 500

mapper.insert:

UPDATE usu_usuario SET
    usu_clave = md5(#{clave})
WHERE USU_COD = #{codigo};


UPDATE usu_usuario SET
    usu_estado = #{estado}
WHERE USU_COD = #{codigo};
